import React from 'react'
import { Card, Image } from 'antd'
import { LikeFilled, EyeFilled } from '@ant-design/icons'
// 프로젝트 데이터의 타입 정의
interface ProjectData {
  projectTitle: string // title을 projectTitle로 변경
  description: string // content를 description으로 변경
  views: number
  likes: number
  generateDate: string // createdAt를 generateDate로 변경
  projectStatus: 'Ps_pr' | 'Ps_co'
  thumbnail?: string
  // projectId: number;
  // userId: string;
  // status: string;
  // recruitmentCount: number; // 새로운 필드 recruitmentCount 추가
  // acceptedID: string; // 새로운 필드 acceptedID 추가
}

const { Meta } = Card

interface ProjectCardProps {
  projectData: ProjectData
}

const ProjectCard: React.FC<ProjectCardProps> = ({ projectData }) => {
  console.log('projectData', projectData)
  const info = projectData?.projectInfo

  function formatDate(date: any) {
    const year = date.getFullYear()
    const month = (date.getMonth() + 1).toString().padStart(2, '0') // 월은 0부터 시작하므로 1을 더하고 2자리 숫자로 만듭니다.
    const day = date.getDate().toString().padStart(2, '0') // 일을 2자리 숫자로 만듭니다.
    return `${year}-${month}-${day}`
  }

  return (
    <Card
      style={{
        maxWidth: '450px',
        minWidth: '300px',
      }}
      // cover={<img alt="example" src="URL_OF_YOUR_THUMBNAIL_IMAGE" />}
      cover={
        info?.thumbnail ? (
          <img
            height={120}
            style={{
              objectFit: 'cover',
            }}
            alt="example"
            src={info?.thumbnail}
          />
        ) : (
          <Image
            preview={false}
            height={120}
            style={{
              objectFit: 'cover',
            }}
            src="error"
            fallback="data:image/png;base64,"
          />
        )
      }
      actions={[
        <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <EyeFilled
            style={{
              marginRight: '10px',
              fontSize: '18px',
            }}
          />
          {`${info?.views}`}
        </div>,
        <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <LikeFilled
            style={{
              marginRight: '10px',
              fontSize: '18px',
            }}
          />
          {`${info?.likes}`}
        </div>,
        <div>{`${formatDate(new Date(info?.generateDate))}`}</div>,
      ]}
    >
      <Meta title={info?.projectTitle} description={info?.description} />
    </Card>
  )
}

export default ProjectCard
